import React, { Component } from 'react';
import Input from './components/input/input';
import './App.css';
import axios from 'axios';
//import BarcodeInputs from './DelBarcode';
//import { CLIENT_RENEG_LIMIT } from 'tls';

class Update extends Component {
    constructor(props) {
        super();
        this.state = {
            ISBN: "",
            Quantity:""
        };
        window.location.reload()
    }
    
    onSubmitHandler = (event) => {
        var data = {
            ISBN: this.state.ISBN,
            Quantity: this.state.Quantity,
           
        }

        axios.post('http://localhost:8081/updatebooks', data)
            .then(res => {
                console.log(res.data);
            })
            .catch((error) => {
                console.log("Error");
            });
        console.log(this.state.Quantity);
        console.log(this.state.barcodes);
    }

    

    render() {

        return (

            <div className="App" >
                <h1 className="Header">UPDATE BOOK</h1>
                <div>
                    <Input
                        inputSize="inputSmall"
                        type="number"
                        placeholder='ISBN'
                        value={this.state.ISBN}
                        changed={e => this.setState({ ISBN: e.target.value })}
                    />
                </div>
                <div>
                    <Input
                        inputSize="inputSmall"

                        type="number"
                        placeholder='Quantity'
                        value={this.state.Quantity}
                        changed={e => this.setState({ Quantity: e.target.value })}
                    />
                </div>
                <br />

                < button onClick={this.onSubmitHandler} > Update</button >
            </div >

        );
    }
}

export default Update;
