import React ,{Component ,useState} from 'react';
import axios from 'axios';


function Search (){
    coonst [output,setOutput]=useState();
const onClickHandler = () =>{
    axios.get('http://localhost:8081/addbooks')
    .then(res =>{
        setOutput(res.data);

    })
    .catch(err =>console.lpg(err));
}

let htmlthing;
if(output) {
    htmlthings=output.map((item,index) => {
        console.log(item);
        return(
            <div>
                <div>Title:{item.Title}</div>
                <div>Author:{item.Author}</div>
                
            </div>
        )
    });
}

return(
    <div>
        <button onClick={onClickHandler}>Search</button>
        <div>

            {htmlthing}
        </div>
    </div>
)
}
 export default search
