import React, {componet}  from 'react';
import Input from './components/input/input';
import './App.css';
import axios from 'axios';
//import Button from './components/Button/Button';
//import axios from 'axios';

 class Login extends Components{
    constructor(props){
        super();
    this.state ={
       Username:"",
       Password:"",
    };
}
    onSubmitHandler=(event)=>{
        console.log("tryiing to login");
        var data={
         Username:this.state.Username,
        Password:this.state.Password,
         
        }

        axios.post('http://localhost:8081/register',data)
        .then(res=>{
            console.log(res.data);
        })
            .catch(error => {
                console.log("Error");
            });
            axios.post('http://localhost:8081/booklist',data)
            .then(res=>{
                console.log(res.data);
            })
                .catch(error => {
                    console.log("Error");
                });
    
}

    render() {
        return (
            
                <div className="App">
                    <h1 className="Header">LOGIN TO LIBRARY SYSTEM</h1>
                    <div>
                <Input 
                    inputSize="inputSmall"
                    type="number" 
                    placeholder='Username'
                    value={this.state.Username}
                    changed={e => this.setState({ISBN:e.target.value})}
                    />
                </div>
                
                
                    
                <div>
                    <Input 
                        inputSize="inputSmall"
                        type="number"
                        placeholder='Password' 
                        value={this.state.Password}
                        changed={e => this.setState({ Title: e.target.value })} 
                        />
                </div>
               
                
               

                    <br/>
                    <br/>
                   <button onClick={this.onSubmitHandler}>Login</button>
                </div>
            
        );
    }
}

export default Login;
