import React ,{Component} from 'react';
import Input from './components/input/input';
import './App.css';
import axios from 'axios';
//import Button from './components/Button/Button';
//import axios from 'axios';

 class Form extends Component{
    constructor(props){
        super();
    this.state ={
        ISBN: "",
        Title: "",
        Author: "",
        Publication: "",
        Barcode: "",
        Quantity:""
    };
}
    onSubmitHandler=(event)=>{
        event.preventDefault();
        const Title =this.state.Title
        console.log("trying to add data");
        var data={
          Barcode:this.state.Barcode,
          ISBN:this.state.ISBN,
         Title:this.state.Title,
          Author:this.state.Author,
          Publication:this.state.Publication,
          Quantity:this.state.Quantity,}

          this.props.onSearchTermcHANGE(Title);
          this.setState({Title:''});




        axios.post('http://localhost:8081/addbooks',data)
        .then(res=>{
            console.log(res.data);
        })
            .catch(error => {
                console.log("Error");
            });
            axios.post('http://localhost:8081/booklist',data)
            .then(res=>{
                console.log(res.data);
            })
                .catch(error => {
                    console.log("Error");
                });

}

    render() {
        return (

                <div className="App">
                    <h1 className="Header">ADD BOOK</h1>
                    <div>
                <Input maxLength={13}
                    inputSize="inputSmall"
                    type="number" 
                    placeholder='ISBN'
                    value={this.state.ISBN}
                    changed={e => this.setState({ISBN:e.target.value})}
                    />
                </div>



                <div>
                    <Input 
                        inputSize="inputSmall"
                        type="text"
                        placeholder='Title' 
                        value={this.state.Title}
                        changed={e => this.setState({ Title: e.target.value })} 
                        />
                </div>
                <div>
                    <Input
                        inputSize="inputSmall"
                        type="text" 
                        placeholder='Author' 
                        value={this.state.Author}
                        changed={e => this.setState({ Author: e.target.value })}
                         />

                </div>
                <div>
                    <Input
                        inputSize="inputSmall"
                        type="text"
                        placeholder='Publication'
                        value={this.state.Publication}
                        changed={event => this.setState({ Publication: event.target.value })} 
                        />
                </div>
                <div>
                <Input 
                    inputSize="inputSmall"
                    type="number" 
                    placeholder='Quantity'
                    value={this.state.Quantity}
                    changed={e => this.setState({Quantity:e.target.value})}
                    />
                </div>
                <div>
                <Input 
                    inputSize="inputSmall"
                    type="number" 
                    placeholder='Barcode'
                    value={this.state.Barcode}
                    changed={e => this.setState({Barcode:e.target.value})}
                    />
                </div>


                    <br/>
                    <br/>
                   <button onClick={this.onSubmitHandler}>ADD</button>
                </div>

        );
    }
}

export default Form;
