import React ,{Component} from 'react';
 import { Navbar, Nav } from 'react-bootstrap';
 import {Button} from 'react-bootstrap';
 import {Form} from 'react-bootstrap';
 import {FormControl} from 'react-bootstrap';
 import {NavLink} from 'react-router-dom';
 import {  withRouter} from 'react-router-dom';
 import './App.css';
 import axios from "ax"

class Home extends Component {
constructor(props){
  super();
  this.state={
    Search:"",
    searchItems:[],
    searchItems:{}
  };
}
onSubmitHandler = event => {
  // <NavLink to='/Search'> </NavLink>
  var data = {
    search: this.state.Search
  };
  console.log(this.state.Search);
  axios
    .get("http://localhost:8081/searchbooks", {
      params: {
        search: this.state.Search
      }
    })
    .then(res => {
      console.log(res.data);
      this.setState({ searchItems: res.data });
      window.result = res.data;
      console.log(window.result);
    })
    .catch(error => {
      console.log("Error");
    });
    this.props.history.push('/search');

};

  

    render() {
        return (
            <div className="App">
            <br />
            <Navbar bg="primary" variant="dark" style={{ minWidth: 700 }}>
              <Navbar.Brand href="#home">Library Management System</Navbar.Brand>
              <Nav className="mr-auto">
                <NavLink to= '/addbook'>
                <Nav.Link href="#addbook">Add Book</Nav.Link></NavLink>
                <NavLink to='/updatebook'>
                <Nav.Link href="#updatebook">Update Book</Nav.Link></NavLink>
                <NavLink to= '/deletebook'>
                <Nav.Link href="#deletebook">Delete</Nav.Link></NavLink>
                <NavLink to= '/studentprofile'>
                <Nav.Link href="#studentprofile">Student Profile</Nav.Link></NavLink>
                <NavLink to= '/issue'>
                <Nav.Link href="#issue">Issue</Nav.Link></NavLink>
             </Nav>
              <Form inline>
            <FormControl
              placeholder="Search"
              value={this.state.Search}
              onChange={e => this.setState({ Search: e.target.value })}
              className="mr_sm-2"
            />
            <Button variant="outline-light" onClick={this.onSubmitHandler}>
              Search
            </Button>
          </Form>
        </Navbar>
        <div className = 'SearchOutput' >
              <div className="search" >
                      <div>SN</div>
                      <div>ISBN</div>
                      <div>Title</div>
                      <div>Author</div>
                      <div>Publication</div>
                      <div>Quantity</div>
                      <div>Type</div>
              </div>
          {this.state.searchItems.map((item, index) => {
            console.log(item);
            return (
              <div className="search" >
                <div>{item.SN}</div>
                <div>{item.ISBN}</div>
                <div>{item.Title}</div>
                <div>{item.Author}</div>
                <div>{item.Publication}</div>
                <div>{item.Quantity}</div>
                <div>{item.Type}</div>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
export default withRouter(Home);
